"use client";
import { useState, useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Image from 'next/image';

gsap.registerPlugin(ScrollTrigger);

export default function EducationPage() {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const educationRef = useRef(null);
  const navRef = useRef(null);
  const timelineRef = useRef(null);

  // Education data
  const education = [
    {
      id: 1,
      degree: "Bachlor of Computer Application",
      institution: "Silver Oak University",
      year: "2022-2025",
      description: "Specialized in Programming Language And Learn New Technologies Used Like Ai and Machine Learning Model",
      icon: "data:image/png;base64,",
      courses: ["Machine Learning", "Other Programming Language", "Web Design"],
    },
    {
      id: 2,
      degree: "Higher Secondary School",
      institution: "Madhav Higher Secondary School ",
      year: "2020-2022",
      description: "Focus on My Higher Secondary Subjects Like Statestical,Accounts,Buisness Administration etc.",
      icon: "data:image/png;base64,",
      courses: ["Buisness Administator", "Economics", "Statestical"],
    },
    {
      id: 3,
      degree: "Secondary School",
      institution: "Jay Somnath Secondary School",
      year: "Jr'kg - 10'th Standard",
      description: "Completed 10th Standard (Secondary School Certificate) with a strong foundation in core academic subjects including Mathematics, Science, Social Studies, and English.",
      icon: "data:image/jpeg;base64,",
      courses: ["English", "Science", "Maths"],
    }
  ];

  const navItems = [
    { name: 'Home', path: '/' },
    { name: 'About', path: '/About' },
    { name: 'Work', path: '/Work' },
    { name: 'Education', path: '/Education' },
    { name: 'Contact', path: '/Contact' }

  ];

  useEffect(() => {
    // Navbar scroll effect
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);

    // Education timeline animation
    const timeline = gsap.timeline({
      scrollTrigger: {
        trigger: ".education-timeline",
        start: "top 70%",
        toggleActions: "play none none none"
      }
    });

    timeline
      .from(".timeline-item", {
        duration: 0.8,
        y: 50,
        opacity: 0,
        stagger: 0.2,
        ease: "power2.out"
      })
      .from(".timeline-connector", {
        duration: 1.2,
        scaleY: 0,
        transformOrigin: "top",
        stagger: 0.2,
        ease: "elastic.out(1, 0.5)"
      }, "-=0.5");

    // Institution icons animation
    gsap.from(".institution-icon", {
      scrollTrigger: {
        trigger: ".education-timeline",
        start: "top 80%"
      },
      duration: 0.6,
      scale: 0,
      stagger: 0.2,
      ease: "back.out(1.7)"
    });

    // Course badges animation
    gsap.from(".course-badge", {
      scrollTrigger: {
        trigger: ".education-timeline",
        start: "top 60%"
      },
      duration: 0.5,
      x: -20,
      opacity: 0,
      stagger: 0.1,
      ease: "power1.out"
    });

    return () => {
      window.removeEventListener('scroll', handleScroll);
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-gray-800 text-gray-100 overflow-x-hidden">
      {/* Navigation Bar */}
      <nav 
        ref={navRef}
        className={`fixed w-full z-50 py-4 px-6 transition-all duration-300 ${
          isScrolled ? 'bg-gray-900/90 backdrop-blur-md py-3' : 'bg-transparent backdrop-blur-none'
        }`}
      >
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <a href="/" className="text-4xl font-serif  text-emerald-400 hover:text-emerald-300 transition-colors">
            Portfolio
          </a>

          {/* Desktop Navigation */}
          <ul className="hidden md:flex space-x-8">
            {navItems.map((item) => (
              <li key={item.name}>
                <a 
                  href={item.path} 
                  className={`text-gray-300 hover:text-emerald-400 transition-colors ${
                    item.path === '/education' ? 'text-emerald-400' : ''
                  }`}
                >
                  {item.name}
                </a>
              </li>
            ))}
          </ul>

          {/* Mobile Menu Button */}
          <button 
            className="md:hidden text-gray-300 focus:outline-none"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label="Toggle menu"
          >
            <div className={`w-6 flex flex-col space-y-1.5 transition-all ${mobileMenuOpen ? 'transform rotate-180' : ''}`}>
              <span className={`block h-0.5 bg-gray-300 transition-all ${mobileMenuOpen ? 'w-6 rotate-45 translate-y-2' : 'w-6'}`}></span>
              <span className={`block h-0.5 bg-gray-300 transition-all ${mobileMenuOpen ? 'opacity-0' : 'w-6'}`}></span>
              <span className={`block h-0.5 bg-gray-300 transition-all ${mobileMenuOpen ? 'w-6 -rotate-45 -translate-y-1' : 'w-6'}`}></span>
            </div>
          </button>
        </div>
      </nav>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="fixed inset-0 z-40 bg-gray-900/95 backdrop-blur-sm flex items-center justify-center">
          <div className="flex flex-col items-center space-y-8">
            {navItems.map((item) => (
              <a 
                key={item.name}
                href={item.path} 
                className="text-3xl text-gray-300 hover:text-emerald-400 transition-colors"
                onClick={() => setMobileMenuOpen(false)}
              >
                {item.name}
              </a>
            ))}
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className="pt-32 pb-20 px-6 max-w-7xl mx-auto">
        {/* Education Header */}
        <section className="mb-20 text-center">
          <h1 className="text-5xl sm:text-6xl font-bold mb-6">
            My <span className="text-emerald-400">Education</span>
          </h1>
          <p className="text-xl text-gray-400 max-w-3xl mx-auto">
            A journey through my academic achievements and continuous learning path in technology.
          </p>
        </section>

        {/* Education Timeline */}
        <section ref={educationRef} className="education-timeline relative">
          {/* Timeline Connector */}
          <div className="absolute left-1/2 transform -translate-x-1/2 w-1 h-full bg-emerald-400/20"></div>

          {/* Timeline Items */}
          <div className="space-y-16">
            {education.map((item, index) => (
              <div 
                key={item.id}
                className={`timeline-item relative ${index % 2 === 0 ? 'pr-10 md:pr-0 md:pl-10' : 'pl-10 md:pl-0 md:pr-10'}`}
              >
                {/* Timeline Dot */}
                <div className={`absolute top-0 w-6 h-6 rounded-full bg-emerald-400 border-4 border-gray-900 transform -translate-y-1/2 ${
                  index % 2 === 0 
                    ? 'left-0 md:left-1/2 md:-translate-x-1/2' 
                    : 'right-0 md:left-1/2 md:-translate-x-1/2'
                }`}></div>

                {/* Timeline Connector */}
                {index < education.length - 1 && (
                  <div className={`timeline-connector absolute ${
                    index % 2 === 0 
                      ? 'left-3 md:left-1/2' 
                      : 'right-3 md:left-1/2'
                  } w-1 h-full bg-emerald-400`}></div>
                )}

                {/* Education Card */}
                <div className={`bg-gray-800/50 border border-gray-700 rounded-xl p-8 shadow-lg transition-all duration-300 hover:border-emerald-400/30 ${
                  index % 2 === 0 
                    ? 'md:mr-auto md:w-5/12' 
                    : 'md:ml-auto md:w-5/12'
                }`}>
                  <div className="flex items-start mb-6">
                    <div className="institution-icon relative w-16 h-16 rounded-full bg-gray-700 flex items-center justify-center overflow-hidden border-2 border-emerald-400/30 mr-6">
                      <Image
                        src={item.icon}
                        alt={item.institution}
                        width={40}
                        height={40}
                        className="object-contain"
                      />
                    </div>
                    <div>
                      <h2 className="text-2xl font-bold text-white">{item.degree}</h2>
                      <h3 className="text-xl text-emerald-400">{item.institution}</h3>
                      <p className="text-gray-400">{item.year}</p>
                    </div>
                  </div>

                  <p className="text-gray-300 mb-6">{item.description}</p>

                  {item.honors && (
                    <div className="bg-emerald-400/10 text-emerald-400 px-4 py-2 rounded-full inline-block mb-6">
                      {item.honors}
                    </div>
                  )}

                  <h4 className="text-lg font-semibold text-white mb-3">Key Courses:</h4>
                  <div className="flex flex-wrap gap-3">
                    {item.courses.map((course) => (
                      <span 
                        key={course} 
                        className="course-badge bg-gray-700 text-gray-300 px-3 py-1 rounded-full text-sm"
                      >
                        {course}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>

        
      </div>
    </div>
  );
}